import React, { useEffect, useRef, useState } from "react";
import "./Timer.css"
import icon from "../../img/timer.png"
import sound from "../../sounds/sound.mp3"

function Timer() {
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [running, setRun] = useState(false);
  const intervalRef = useRef(null);

  function start() {
    setRun(true);
    // Устанавливаем интервал, который будет уменьшать количество секунд каждую секунду
    intervalRef.current = setInterval(() => {
      setSeconds(prevSeconds => {
        if (prevSeconds === 0) {
          // Если количество секунд равно нулю, уменьшаем количество минут на 1 и устанавливаем количество секунд в 59
          setMinutes(prevMinutes => {
            if (prevMinutes === 0) {
              // Если количество минут равно нулю, останавливаем таймер
              setRun(false);
              clearInterval(intervalRef.current);
              // Запускаем звуковой эффект
              const audio = new Audio(sound);
              audio.play();
              return prevMinutes;
            }
            return prevMinutes - 1;
          });
          return 59;
        }
        return prevSeconds - 1;
      });
    }, 1000);
  }

  function stop() {
    setRun(false);
    clearInterval(intervalRef.current);
  }

  function reset() {
    setRun(false);
    clearInterval(intervalRef.current);
    setMinutes(0);
    setSeconds(0);
    // Возвращаем дефолтный цвет фона
    document.body.style.backgroundColor = "#fff";
    // Останавливаем звуковой эффект
    const audio = new Audio(sound);
    audio.pause();
    audio.currentTime = 0;
  }

  function handlerMinutesChange(event) {
    if (!running) {
      setMinutes(parseInt(event.target.value));
    }
  }

  function handlerSecondsChange(event) {
    if (!running) {
      setSeconds(parseInt(event.target.value));
    }
  }

  useEffect(() => {
    // Восстанавливаем сохраненное значение минут
    const savedMinutes = localStorage.getItem("minutes");
    if (savedMinutes) {
      setMinutes(parseInt(savedMinutes));
    }
    // Добавляем обработчик события перед выходом из приложения
    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => {
      // Удаляем обработчик события перед выходом из приложения
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    // Сохраняем значение минут и секунд в локальное хранилище
    const storedMinutes = localStorage.getItem('minutes');
    const storedSeconds = localStorage.getItem('seconds');

    if (storedMinutes && storedSeconds) {
      setMinutes(Number(storedMinutes));
      setSeconds(Number(storedSeconds));
    }
  }, []);

  useEffect(() => {
    // Обновляем значения минут и секунд в инпутах в реальном времени
    if (running) {
      document.title = `${minutes.toString().padStart(2, "0")}:${seconds
        .toString()
        .padStart(2, "0")}`;
    } else {
      document.title = "Таймер";
    }
    document.body.style.backgroundColor = "#fff";
    if (minutes === 0 && seconds === 0) {
      // Если количество минут и секунд равно нулю, изменяем цвет фона на красный
      document.body.style.backgroundColor = "#ff0000";
    }
  }, [minutes, seconds, running]);

  const [state, setState] = useState(null);

  useEffect(() => {
    // Восстанавливаем сохраненное состояние таймера
    const savedState = localStorage.getItem("state");
    if (savedState) {
      const { minutes, isRunning } = JSON.parse(savedState);
      setState({ minutes, isRunning });
    }
    // Добавляем обработчик события перед выходом из приложения
    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => {
      // Удаляем обработчик события перед выходом из приложения
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    // Сохраняем текущее состояние таймера в локальное хранилище
    if (state) {
      localStorage.setItem("state", JSON.stringify(state));
    }
  }, [state]);

  useEffect(() => {
    function handleBeforeUnload(event) {
      // Сохраняем текущее состояние таймера перед выходом из приложения
      const message = JSON.stringify(state);
      event.returnValue = message;
      return message;
    }
  }, [minutes, seconds]);

  useEffect(() => {
  function handleLoadState() {
    // Загружаем сохраненное состояние таймера
    const savedState = localStorage.getItem("state");
    if (savedState) {
      const { minutes: savedMinutes, seconds: savedSeconds } = JSON.parse(savedState);
      setMinutes(savedMinutes);
      setSeconds(savedSeconds);
    }
  }
  }, []);


  return (
    <div className="timer-container">
      <h1>Таймер обратного отсчета</h1>
      {state ? (
        <Timer
          minutes={state.minutes}
          isRunning={state.isRunning}
          onStop={() => setState(null)}
        />
      ) : (
        <div>
          <button onClick={handleLoadState}>Загрузить состояние</button>
          <button onClick={handleClearState}>Очистить состояние</button>
        </div>
      )}
      <div className="input-container">
        <input
          type="number"
          min="0"
          max="60"
          value={minutes}
          onChange={handlerMinutesChange}
          disabled={running}
        />
        <p>минуты</p>
        <input
          type="number"
          min="0"
          max="60"
          value={seconds}
          onChange={handlerSecondsChange}
          disabled={running}
        />
        <p>секунды</p>
      </div>
      <div className="button__container">
        {!running && (
          <button className="button" onClick={start}>
            Старт
          </button>
        )}
        {running && (
          <button className="button" onClick={stop}>
            Стоп
          </button>
        )}
        <button className="button" onClick={reset}>
          Сброс
        </button>
      </div>
    </div>
  );
}

export default Timer;
