import React, { useState, useEffect } from 'react';

const Timer = () => {
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [status, setStatus] = useState('stopped');
  const [soundPlaying, setSoundPlaying] = useState(false);

  const startTimer = () => {
    setStatus('running');
  };

  const stopTimer = () => {
    setStatus('stopped');
  };

  const resetTimer = () => {
    setStatus('stopped');
    setMinutes(0);
    setSeconds(0);
    setSoundPlaying(false);
  };

  const tick = () => {
    if (status === 'running') {
      if (seconds === 0 && minutes === 0) {
        setSoundPlaying(true);
        setStatus('finished');
      } else if (seconds === 0) {
        setMinutes(minutes - 1);
        setSeconds(59);
      } else {
        setSeconds(seconds - 1);
      }
    }
  };

  const playSound = () => {
    const audio = new Audio('../../sounds/sound.mp3');
    audio.play();
  };

  useEffect(() => {
    const timerId = setInterval(() => tick(), 1000);
    return () => clearInterval(timerId);
  }, [minutes, seconds, status]);

  useEffect(() => {
    if (soundPlaying) {
      playSound();
    }
  }, [soundPlaying]);

  const handleMinutesChange = (event) => {
    if (status === 'stopped') {
      setMinutes(parseInt(event.target.value));
    }
  };

  const handleSecondsChange = (event) => {
    if (status === 'stopped') {
      setSeconds(parseInt(event.target.value));
    }
  };

  const handleStartButtonClick = () => {
    startTimer();
  };

  const handleStopButtonClick = () => {
    stopTimer();
  };

  const handleResetButtonClick = () => {
    resetTimer();
  };

  const minutesDisplay = minutes < 10 ? `0${minutes}` : minutes;
  const secondsDisplay = seconds < 10 ? `0${seconds}` : seconds;

  

  const backgroundColor = status === 'finished' ? 'red' : 'white';

  return (
    <div style={{ backgroundColor }}>
      <div>
        <input type="number" min="0" max="60" value={minutesDisplay} onChange={handleMinutesChange}  />
        <span>:</span>
        <input type="number" min="0" max="60" value={secondsDisplay} onChange={handleSecondsChange}  />
      </div>
      <div>
        <button onClick={handleStartButtonClick} >
          Start
        </button>
        <button onClick={handleStopButtonClick} >
          Stop
        </button>
        <button onClick={handleResetButtonClick}>Reset</button>
      </div>
    </div>
  );
};

export default Timer;
