import React, { useEffect, useState } from "react";
import stl from "./Timer.css"
import icon from "../../img/timer.png"
import sound from "../../sounds/sound.mp3"

const TimerView = () => {
  const endSound = new Audio(sound);
  const [minutes, setMinutes] = useState(3);
  const [seconds, setSeconds] = useState(0);
  const [flag, setFlag] = useState(false);
  const [inputDisable, setInputDisable] = useState(false);
  const [pauseDisable, setPauseDisable] = useState(true);
  const [continueDisable, setContinueDisable] = useState(true);
  
  const start = () => {
    setFlag(true);
    setInputDisable(true);
    setPauseDisable(false);
  };
  const reset = () => {
    setFlag(false);
    setInputDisable(false);
    setSeconds(0);
    setMinutes(0);
    setPauseDisable(true);
    setContinueDisable(true);
    endSound.pause();
    endSound.currentTime = 0;
    document.body.style.backgroundColor = "#B4C4FF";
  };

  const stop = () => {
    setFlag(false);
    setPauseDisable(true);
    setContinueDisable(false);
    endSound.pause();
    endSound.currentTime = 0;
  };

  useEffect(() => {
    if (flag) {
      const interval = setInterval(() => {
        if (parseInt(seconds) == 0 && parseInt(minutes) !== 0) {
          setSeconds((seconds) => seconds + 59);
          setMinutes((minutes) => minutes - 1);
        } else if (parseInt(seconds) == 0 && parseInt(minutes) == 0) {
        } else {
          setSeconds((seconds) => seconds - 1);
        }
      }, 1000);

      return () => {
        clearInterval(interval);
        document.body.style.backgroundColor = "#FF8888";
        endSound.play();
      };
    }
  }, [seconds, minutes, flag]);

  useEffect(() => {
    const handleBeforeUnload = () => {
        localStorage.setItem('timerState', JSON.stringify({ minutes, seconds }));
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
        window.removeEventListener('beforeunload', handleBeforeUnload);
    };
}, [minutes, seconds]);

useEffect(() => {
    const savedTimerState = localStorage.getItem('timerState');

    if (savedTimerState) {
        const { minutes: savedMinutes, seconds: savedSeconds } = JSON.parse(savedTimerState);
        setMinutes(savedMinutes);
        setSeconds(savedSeconds);
    }
}, []);

const handleMinutesChange = (event) => {
    if (!isActive) {
        setMinutes(parseInt(event.target.value));
    }
};
const handleSecondsChange = (event) => {
    if (!isActive) {
        setSeconds(parseInt(event.target.value));
    }
};

return (
    <section className={styles.timer}>
        <div className={styles.timer__block}>
            <div className={styles.timer__container}>
                <div className={styles.timer__title}>
                    <span>
                        Timer
                    </span>
                    <img src={timerIcon} alt="timer" />
                </div>
                <div className={styles.timer__content}>
                    <div className={styles.timer__inner_container}>
                        <div className={styles.timer__input}>
                            <input
                                type="number"
                                value={minutes}
                                onChange={handleMinutesChange}
                                disabled={isActive}
                                min="0"
                            />
                            <span>minutes</span>
                        </div>
                        <div className={styles.timer__input}>
                            <input
                                type="number"
                                value={seconds}
                                onChange={handleSecondsChange}
                                disabled={isActive}
                                min="0"
                                max="59"
                            />
                            <span>seconds</span>
                        </div>
                        <div className={styles.timer__btns}>
                            <button onClick={startTimer} disabled={isActive}>
                                Start
                            </button>
                            <button onClick={stopTimer} disabled={!isActive}>
                                Stop
                            </button>
                            <button onClick={resetTimer}>Reset</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </section>

);
};

export default TimerView;
