import React, { useEffect, useRef, useState } from "react";
import stl from "./Timer.css"
import icon from "../../img/timer.png"
import sound from "../../sounds/sound.mp3"

function Timer() {
  const endSound = useRef(null);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [running, setRun] = useState(false);
  const [stoped, setStop] = useState(false);
  const [id, setId] = useState(null);
  const [inputDisable, setInputDisable] = useState(false);
  const [pauseDisable, setPauseDisable] = useState(true);
  const [continueDisable, setContinueDisable] = useState(true);
  
  const start = () => {
    if (!running) {
      setRun(true);
      setId(
        setInterval(() => {
          setSeconds((prevSeconds) => {
            if (prevSeconds === 59) {
              setMinutes((prevMinutes) => prevMinutes + 1);
              return 0;
            }
            return prevSeconds + 1;
          });
        }, 1000)
      );
    }
  };

  const stop = () => {
    setRun(false);
    setPauseDisable(true);
    setContinueDisable(false);
  };

  const reset = () => {
    setRun(false);
    setStop(false);
    setInputDisable(false);
    clearInterval(id);
    setId(null);
    setSeconds(0);
    setMinutes(0);
    setPauseDisable(true);
    setContinueDisable(true);
    endSound.current.pause();
    endSound.currentTime = 0;
    document.body.style.backgroundColor = "white";
  };

  const handlerMinutesChange = (event) => {
    if (!running) {
      setMinutes(parseInt(event.target.value));
    }
  };

  const handlerSecondsChange = (event) => {
    if (!running) {
      setSeconds(parseInt(event.target.value));
    }
  };

  const padNumber = (number) => {
    return number.toString().padStart(2, "0");
  };

  const handlerStop = () => {
    setStop(true);
    endSound.current.play();
    document.body.style.backgroundColor = "red";
  };

  const renderInputs = () => {
    return (
      <>
        <input type="number" value={minutes} onChange={handlerMinutesChange} />
        <span>:</span>
        <input type="number" value={seconds} onChange={handlerSecondsChange} />
      </>
    );
  };

  const renderButtons = () => {
    return (
      <>
        <button onClick={start} disabled={running}>Start</button>
        <button onClick={stop} disabled={!running}>Stop</button>
        <button onClick={reset}>Reset</button>
      </>
    );
  };

  const renderAudio = () => {
    return (
      <audio
        src={sound}
        ref={endSound}
        onEnded={reset}
      />
    );
  };

  const render = () => {
    if (stoped) {
      return (
        <>
          <span>Timer finished!</span>
          <br />
          {renderButtons()}
          {renderAudio()}
        </>
      );
    } else {
      return (
        <>
          {renderInputs()}
          <br />
          {renderButtons()}
        </>
      );
    }
  };
  return <div>{render()}</div>;
}

export default Timer;
