import React from 'react';
import './TodoItem.css';

const TodoItem = ({ todo, onDelete }) => {
  const { id, title, description, date } = todo;

  return (
    <li>
      <h3>{title}</h3>
      <p>{description}</p>
      <p>{date}</p>
      <button onClick={() => onDelete(id)}>Delete</button>
    </li>
  );
};

export default TodoItem;
